import { assign, createMachine, Sender } from "xstate";
import { FetchMachineContext, FetchMachineEvent } from "./fetchState";

export const fetchMachine =

/** @xstate-layout -IJAA */
createMachine< FetchMachineContext, FetchMachineEvent>(
  {
    id: "fetch",
    initial: "idle",

    context: {
      url: "",
      code: 200,
      data: "",
    },

    states: {
      "idle": {},
      "pending": {
        on: {
          FETCH: {},
        },
        invoke: {
          id: "fetchData",
          src: "fetchData",
          onDone: {
            target: "success",
            actions: "saveData",
          },
          onError: {
            target: "error",
          },
        },
      },
      "success": {},
      "error": {},
    },

    on: {
      FETCH: {
        target: "pending",
      }
    }
  },
  {
    services: {
      fetchData: (ctx, event) => async (send: Sender<FetchMachineEvent>) => {
        console.log('Fetching: ', {ctx, event});
        return fetch(event.data.url).then((res) => res.json());
      },
    },
    actions: {
      saveData: assign({
        data: (_, event) => event.data,
      }),
    }
  }
)
